// PDF.js Worker - version simplifiée pour développement local
// En production, remplacer par le fichier officiel PDF.js worker

console.log('PDF.js Worker chargé - version développement');

// Simulation basique du worker PDF.js pour éviter les erreurs de chargement
if (typeof importScripts !== 'undefined') {
  // Context Web Worker
  self.onmessage = function(e) {
    console.log('PDF Worker message reçu:', e.data);
    // Réponse basique
    self.postMessage({ type: 'ready' });
  };
} else {
  // Context principal - export pour compatibilité
  window.pdfjsWorker = {
    version: '3.11.174',
    loaded: true
  };
}